@page {
    size: 9in 5in;
    margin: 0;
}

body {
    margin: 0;
    padding: 0;
    background: #e7eaf0;
    font-family: 'Open Sans', Arial, sans-serif;
}

.label-container {
    width: 9in;
    height: 5in;
    margin: 0 auto;
    background: url("https://firebasestorage.googleapis.com/v0/b/specchem-pwa-feb-25.firebasestorage.app/o/branding%2F5x9-label-template.png?alt=media&token=afc42346-5527-4c06-8c50-1aaef543e1ef") no-repeat center center;
    background-size: cover;
    position: relative;
    display: flex;
    flex-direction: column;
    overflow: hidden;
    box-sizing: border-box;
}

.watermark {
    position: absolute;
    top: 54%;
    left: 50%;
    transform: translate(-50%, -50%);
    width: 215px;
    opacity: 0.14;
    z-index: 1;
    pointer-events: none;
    mix-blend-mode: multiply;
    filter: blur(0.2px);
    max-width: 60%;
}

.columns-container {
    position: relative;
    display: flex;
    justify-content: space-between;
    align-items: flex-start;
    width: 100%;
    height: 100%;
    padding: 0.33in 0.36in 0.11in 0.36in;
    box-sizing: border-box;
    z-index: 2;
}

.left-columns, .right-columns {
    width: 32%;
    min-width: 130px;
    max-width: 33%;
    line-height: 1.16;
    word-break: break-word;
}

.left-columns {
    color: #18335b;
    margin-top: .2in;
    column-count: 2;
    column-gap: 0.19in;
}

.right-columns {
    color: #232942;
    font-size: 4.7px;
    line-height: 1.12;
    margin-top: .5in;
    column-count: 2;
    column-gap: 0.19in;
}

.contact-block {
    font-size: 4px;
    line-height: 1.25;
    margin-bottom: 2px;
    color: #1a2340;
    font-family: 'Open Sans', Arial, sans-serif;
    word-break: break-word;
}

.left-columns h4 {
    font-family: 'Montserrat', 'Open Sans', Arial, sans-serif;
    font-size: 8.8px;
    color: #1e3369;
    text-transform: uppercase;
    font-weight: 700;
    margin-bottom: 0.5px;
    margin-top: 11px;
    letter-spacing: 0.07em;
    padding-left: 5px; 
}

.left-columns p, .left-columns ul, .left-columns li {
    font-size: 6.2px;
    color: inherit;
    font-family: 'Montserrat', 'Open Sans', Arial, sans-serif;
    margin: 0 0 3px 0;
}

.left-columns ul {
    padding-left: 12px;
}

.right-columns h4 {
    font-family: 'Montserrat', Arial, sans-serif;
    font-size: 6.3px;
    color: #233066;
    text-transform: uppercase;
    font-weight: 700;
    margin-bottom: 0.5px;
    margin-top: 4px;
    letter-spacing: 0.04em;
}

.right-columns p,
.right-columns ul,
.right-columns li,
.right-columns .statement-list {
    font-size: 4px;
    color: inherit;
    font-family: 'Open Sans', Arial, sans-serif;
    margin: 0 0 1.4px 0;
    list-style: none;
    padding-left: 0;
}

.right-columns .statement-list {
    margin-bottom: 0.5em;
}

.right-columns .statement-list > div {
    margin-bottom: 1px;
}

.right-columns .signal-word {
    font-size: 4.7px;
    font-weight: 700;
    font-family: 'Montserrat', Arial, sans-serif;
    margin-top: 0;
    margin-bottom: 0.6px;
}

.center-content {
    width: 31%;
    min-width: 110px;
    max-width: 32%;
    display: flex;
    flex-direction: column;
    align-items: center;
    text-align: center;
    z-index: 5;
    position: absolute;
    left: 50%;
    transform: translateX(-50%);
    top: 120px;
}

.center-content .product-name {
    font-family: 'Montserrat', Arial, sans-serif;
    font-size: 24px;
    font-weight: 700;
    color: #21325b;
    letter-spacing: 0.01em;
    margin: 0 0 2px 0;
    line-height: 1.1;
}

.short-description-english {
    font-family: 'Montserrat', Arial, sans-serif;
    font-size: 12px;
    font-weight: 600;
    color: #2453a6;
    margin: 0 0 1.7px 0;
    letter-spacing: 0.01em;
}

.translated-short-description {
    font-size: 8px;
    font-family: 'Open Sans', Arial, sans-serif;
    color: #395073;
    font-weight: 500;
    margin: 0 0 1.3px 0;
}

.logo-container {
    position: absolute;
    left: 50%;
    transform: translateX(-50%);
    bottom: 38%;
    margin: 0;
    width: 100%;
    display: flex;
    justify-content: center;
}

.logo-container img {
    width: 150px;
    filter: drop-shadow(0 2px 8px #132e5712);
}

.center-bottom-content {
    display: flex;
    flex-direction: row;
    gap: 8px;
    justify-content: space-between;
    align-items: center;
    margin-top: 17px;
    width: 100%;
}

.pictograms {
    display: flex;
    gap: 4px;
    align-items: center;
    margin: 0 0 3px 0;
}

.pictograms img {
    width: 13px;
    height: 13px;
    object-fit: contain;
    border-radius: 3px;
    border: 1px solid #e3e8f1;
    background: #f7fafc;
}

.label-bottom-bar {
    position: absolute;
    left: .1in;
    bottom: .3in;
    width: 100%;
    display: flex;
    align-items: flex-end;
    z-index: 15;
    height: 88px;
}

.corner-icon-danger {
    width: 100px;
    height: 60px;
    object-fit: contain;
    background: #fff;
    border-radius: 12px;
    box-shadow: 0 2px 6px #22316622;
    padding: 4px;
    margin-left: 4px;
}

.corner-icon {
    width: 60px;
    height: 60px;
    object-fit: contain;
    background: #fff;
    border-radius: 12px;
    box-shadow: 0 2px 6px #22316622;
    padding: 4px;
    margin-left: 13px;
}

.code-row {
    position: absolute;
    left: 50%;
    bottom: 0.53in;
    transform: translateX(-50%);
    width: auto;
    z-index: 20;
    display: flex;
    flex-direction: row;
    align-items: flex-start;
    justify-content: center;
    gap: 35px;
}

.code-info {
    display: flex;
    flex-direction: column;
    align-items: flex-start;
    justify-content: center;
    font-size: 13px;
    color: #1b2754;
    font-family: 'Open Sans', Arial, sans-serif;
    gap: 4px;
    min-width: 175px;
}

.batch-info-column {
    display: flex;
    flex-direction: column;
    gap: 4px;
}

.batch-field,
.package-size,
.use-by {
    text-align: left;
    margin-left: 0;
    font-size: 13px;
    color: #1b2754;
    font-family: 'Open Sans', Arial, sans-serif;
}

.batch-field label {
    display: inline-block;
    min-width: 74px;
    padding: 0;
    margin: 0;
}

.batch-field input {
    width: 84px;
    font-size: 13px;
    border: none;
    border-bottom: 1.5px solid #223166;
    background: #f3f7ff;
    outline: none;
    padding: 2px 3px;
    border-radius: 2.5px 2.5px 0 0;
    margin-left: 0;
    vertical-align: middle;
}

.qr-code {
    width: 38px;
    height: 38px;
    flex-shrink: 0;
    filter: drop-shadow(0 2px 4px #25408014);
}

.green-conscious-icon {
    width: 38px;
    position: absolute;
    right: -15%;
    top: 50%;
    transform: translateY(-50%);
}

/* Manufacturing and Safety Notice Styles */
.manufacturing-safety-notices {
    margin-top: 4px;
}

.manufacturing-safety-notices .rc-section {
    margin-bottom: 2px;
}

.manufacturing-safety-notices .rc-section h4 {
    font-size: 5px;
    margin-bottom: 0.5px;
    margin-top: 2px;
}

.manufacturing-safety-notices .rc-section p {
    font-size: 3.5px;
    line-height: 1.1;
    margin: 0 0 1px 0;
    font-weight: 500;
}

.manufacturing-notice {
    color: #1a5b2e !important;
    font-weight: 600 !important;
}

.safety-notice {
    font-weight: 600 !important;
}

/* Manufacturing Footer */
.manufacturing-footer {
    position: absolute;
    bottom: 0.02in;
    left: 50%;
    transform: translateX(-50%);
    z-index: 25;
    text-align: center;
}

.manufacturing-footer .manufacturing-notice {
    font-size: 6px;
    font-family: 'Lato', Arial, sans-serif;
    color: #1a5b2e;
    font-weight: 600;
    margin: 0;
}

/* Warranty/Conditions Styles */
.warranty-conditions-right {
    margin-top: 4px;
}

.warranty-conditions-right .rc-section {
    margin-bottom: 2px;
}

.warranty-conditions-right .rc-section h4 {
    font-size: 5px;
    margin-bottom: 0.5px;
    margin-top: 2px;
}

.warranty-conditions-right .rc-section p {
    font-size: 3.2px;
    line-height: 1.1;
    margin: 0 0 1px 0;
}

.product-name-badge-wrap {
    display: flex;
    align-items: baseline;
    justify-content: center;
    gap: 7px;
}

.product-name-badge-wrap img {
    width: 30px;
    height: 30px;
    margin-top: 2px;
}

.eco-badge {
    margin-top: -10px;
    vertical-align: baseline;
}

.center-content .subtitle {
    font-family: 'Montserrat', Arial, sans-serif;
    font-size: 10px;
    color: #294b88;
    font-weight: 500;
    letter-spacing: 0.015em;
    margin: 1px 0 1px 0;
}

.center-content .subtitle.subtitle-2 {
    font-size: 8px;
    color: #466db2;
    font-weight: 400;
    margin-bottom: 1px;
}

@media print {
    body {
        -webkit-print-color-adjust: exact;
        print-color-adjust: exact;
        background: none !important;
    }
    .label-container { 
        box-shadow: none !important;
        border-radius: 0 !important;
    }
}

